package com.letv.rn.focusableView;

import com.facebook.react.uimanager.ThemedReactContext;
import com.facebook.react.uimanager.ViewGroupManager;
import com.facebook.react.uimanager.annotations.ReactProp;

/**
 * Manages instances of LeImageButton.
 */
public class LeViewManager extends ViewGroupManager<LeView> {

    private static final String REACT_CLASS = "LeView";

    @Override
    public String getName() {

        return REACT_CLASS;
    }

    @Override
    protected LeView createViewInstance(ThemedReactContext reactContext) {
        return new LeView(reactContext);
    }

    @ReactProp(name = "focus", defaultBoolean = false)
    public void setSource(LeView view, boolean focus) {
        view.SetFocus(focus);
    }

    @ReactProp(name = "isWrapper", defaultBoolean = false)
    public void setIsWrapper(LeView view, boolean isWrapper) {
        view.SetIsWrapper(isWrapper);
    }
}